import React from 'react';
import { View, Text, ScrollView, StyleSheet } from 'react-native';
import Button from '../../Components/Button';

export default function Sale() {
 return (
   <ScrollView style={styles.container}>
      <View style={styles.textContainer}>
          <View style={{marginTop: '10%'}}>
          <Text style={styles.title}>DADOS DO CLIENTE</Text>
          </View>
          <View style={styles.box}>
            <Text style={styles.text}>Nome</Text>
          </View>
          <View style={styles.box}>
            <Text style={styles.text}>Whatsapp</Text>
          </View>
          <View style={styles.box}>
            <Text style={styles.text}> Endere√ßo</Text>
          </View>
          <View style={styles.box}>
            <Text style={styles.text}> CEP</Text>
          </View>
          <View style={[styles.box, {height:'20%'}, {borderRadius:20}, {justifyContent:'flex-start'}]}>
            <Text style={[styles.text, {marginTop:'3%'}]}> Observa√ß√µes</Text>
          </View>

          <View style={styles.titleContainer}>
            <Text style={styles.title}>FORMA DE PAGAMENTO</Text>
          </View>
          <View style={styles.box}>
            <Text style={styles.text}>Em dinheiro</Text>
          </View>
          <View style={styles.box}>
            <Text style={styles.text}>Troco para quanto?</Text>
          </View>

          <View style={styles.box}>
            <Text style={styles.text}>CONFIRMAR PEDIDO</Text>
          </View>
      </View>
   </ScrollView>

  );
}

const styles = StyleSheet.create({
  container:{
    flex: 1,
    width: '100%',
    backgroundColor: '#FFF',
  },
  textContainer:{
    alignItems: 'center',
    justifyContent:'center',
    marginBottom:'20%'
  },
  titleContainer:{
    alignItems: 'center',
    justifyContent:'center',
    
  },
  title:{
    fontSize: 22,
    fontFamily:'Anton_400Regular',
    marginVertical: '5%'
  },
  box:{
    backgroundColor: '#f5f5f5',
    borderRadius: 50,
    marginVertical: '3%',
    width: '95%',
    height: 40,
    justifyContent:'center'
  },
  text:{
    fontSize: 18,
    marginHorizontal: '5%',
    color: 'grey'
  }
});
