import { NavigationRouteContext } from '@react-navigation/native';
import React from 'react';
import { View, Text, Image, ScrollView, TouchableOpacity, StyleSheet} from 'react-native';
import Button from '../../Components/Button';
import {useNavigation} from '@react-navigation/native'

export default function Detail() {
  const navigator = useNavigation();

 return (
    <ScrollView style={styles.container}>
        <Image
        source={require('../../assets/sombrarosa')}
        style={styles.image}
        resizeMode='cover'
        />

        <View>
          <Text style={styles.title, {fontSize: 24}}>R$25.00</Text>
          <Text style={styles.title, {fontSize: 24}}>Sombra Rosa Cheiro de Flor</Text>
        </View>

        <View style={styles.textContent}>
          <Text style={styles.textContent}>Descrição</Text>
        </View>

        <Button onClick2={()=> navigation.navigate('Sale')}/>
    </ScrollView>

    
  );
}

const styles = StyleSheet.create({
  container:{
    flex: 1,
    width: '100%',
    backgroundColor:'#FFF'
  },
  image:{
    width: '100%'
  },
  title:{
    fontFamily: 'PlayfairDisplay_400Regular',
    paddingHorizontal: '2%'
  },
  textContent:{
    fontSize: 16,
    lineHeight: 25,
    marginVertical: '2%',
    marginHorizontal: '2%'
  }
})
